var arr = [
  {
    songName: "Die For You",
    url: "./assets/sound/The Weeknd - Die For You.mp3",
    img: "./assets/images/die-4-u.jpg",
    time: "3:52",
  },
  {
    songName: "Delicate",
    url: "./assets/sound/Taylor Swift -Delicate.mp3",
    img: "./assets/images/End-Game.webp",
    time: "3:54",
  },
  {
    songName: "End Of Beginning",
    url: "./assets/sound/Djo - End Of Beginning.mp3",
    img: "./assets/images/Djo-End-of-beginning.webp",
    time: "0:14",
  },
  {
    songName: "Hisaab",
    url: "./assets/sound/HISAAB - DIVINE, KARAN AUJLA.mp3",
    img: "./assets/images/hisaab-karan-aujla.webp",
    time: "3:47",
  },
  {
    songName: "Saya Mera",
    url: "./assets/sound/Bella - Saya Mera.mp3",
    img: "./assets/images/bella.jpg",
    time: "2:53",
  },
];
var allsongs = document.querySelector("#all-songs");
var audio = new Audio();
var selectedSong = 0;
var poster = document.querySelector("#left");
var play = document.querySelector("#play");
var backward = document.querySelector("#backward");
var forword = document.querySelector("#forward");

// Define loop and repeat all variables
var loop = false;
var repeatAll = false;

// Add buttons for loop and repeat all
var loopBtn = document.querySelector("#loop");
var repeatAllBtn = document.querySelector("#repeat-all");

audio.addEventListener("ended", function () {
  if (loop) {
    audio.currentTime = 0;
    audio.play();
  } else {
    if (selectedSong < arr.length - 1) {
      selectedSong++;
      mainfunction();
      play.innerHTML = `<i class="ri-pause-mini-fill"></i>`;
      backward.style.opacity = 1;
      audio.play();
      flag = 1;
    } else {
      if (repeatAll) {
        selectedSong = 0;
        mainfunction();
        play.innerHTML = `<i class="ri-pause-mini-fill"></i>`;
        backward.style.opacity = 1;
        audio.play();
        flag = 1;
      }
    }
  }
});

// Function to toggle loop
loopBtn.addEventListener("click", function () {
  loop = !loop;
  loopBtn.classList.toggle("active", loop);
});

// Function to toggle repeat all
repeatAllBtn.addEventListener("click", function () {
  repeatAll = !repeatAll;
  repeatAllBtn.classList.toggle("active", repeatAll);
});

function mainfunction() {
  var clutter = "";
  arr.forEach(function (elem, index) {
    clutter += `<div class="song-card" id="${index}">
    <div id="part1">
      
      <img
        src="${elem.img}"
        alt=""
      />
      <h2>${elem.songName}</h2>
    </div>
    <h6>${elem.time}</h6>
  </div>`;
  });
  audio.src = arr[selectedSong].url;
  allsongs.innerHTML = clutter;
  poster.style.backgroundImage = `url(${arr[selectedSong].img})`;
}
mainfunction();
allsongs.addEventListener("click", function (details) {
  selectedSong = details.target.id;
  mainfunction();
  audio.play();
  play.innerHTML = `<i class="ri-pause-mini-fill"></i>`;
  backward.style.opacity = 1;
  forward.style.opacity = 1;
  flag = 1;
});
var flag = 0;
play.addEventListener("click", function () {
  if (flag == 0) {
    play.innerHTML = `<i class="ri-pause-mini-fill"></i>`;
    audio.play();
    flag = 1;
  } else {
    play.innerHTML = `<i class="ri-play-mini-fill"></i>`;
    audio.pause();
    flag = 0;
  }
});
forword.addEventListener("click", function () {
  if (selectedSong < arr.length - 1) {
    selectedSong++;
    mainfunction();
    play.innerHTML = `<i class="ri-pause-mini-fill"></i>`;
    backward.style.opacity = 1;
    audio.play();
    flag = 1;
  } else {
    forword.style.opacity = 0.4;
  }
});
backward.addEventListener("click", function () {
  if (selectedSong > 0) {
    selectedSong--;
    mainfunction();
    play.innerHTML = `<i class="ri-pause-mini-fill"></i>`;
    forward.style.opacity = 1;
    audio.play();
    flag = 1;
  } else {
    backward.style.opacity = 0.4;
  }
});
